import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";


// IMPORT COMPONENT
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

// import TechCreateNewsApi from "../admin-screen/TechCreateNewsApi";
import { advertiseDetailAction } from "../actions/advertiseActions";
import { advertiseUpdateAction } from "../actions/advertiseActions";
import { ADVERTISE_UPDATE_RESET } from "../constants/productivityConstants";

import "../css_styles/TechCreate.css";

const AdvertiseUpdate = () => {
  const params = useParams();
  const {advertise_id, advertise_slug} = params.id;
  const navigate = useNavigate();



  const [category, setCategory] = useState("");
  const [country, setCountry] = useState("");
  const [state, setState] = useState("");
  const [address, setAddress] = useState("");
  const [contact, setContact] = useState("");
  const [image, setImage] = useState("");
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const dispatch = useDispatch();
  const advertiseDetail = useSelector((state) => state.advertiseDetail);
  const {
    error:     detailAdvertiseError,
    loading:   detailAdvertiseLoading,
    advertise: detailAdvertise,
  } = advertiseDetail;

  const advertiseUpdate = useSelector((state) => state.advertiseUpdate);
  const {
    error:     updateAdvertiseError,
    loading:   updateAdvertiseLoading,
    success:   updateAdvertiseSuccess,
    advertise: updateAdvertise,
  } = advertiseUpdate;


  useEffect(() => {
    if (updateAdvertiseSuccess) {
      dispatch({ type: ADVERTISE_UPDATE_RESET });
      if (userInfo.isAdmin){
        navigate("/admin-dashboard");

      } else{
        navigate("/advertise-create")
      }
    } else {
      if (
        !detailAdvertise.title ||
        detailAdvertise.id !== Number(advertise_id)
      ) {
        dispatch(advertiseDetailAction(advertise_id));
      } else {
        setCategory(detailAdvertise.category);
        setCountry (detailAdvertise.country);
        setState   (detailAdvertise.state);
        setAddress (detailAdvertise.address);
        setContact (detailAdvertise.contact);
        setImage   (detailAdvertise.image);
        setTitle   (detailAdvertise.title);
        setContent (detailAdvertise.content);
      }
    }
  }, [dispatch, advertise_id, detailAdvertise,updateAdvertiseSuccess, updateAdvertise]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(
        advertiseUpdateAction({
        id: advertise_id,
        category,
        country,
        state,
        address,
        contact,
        image,
        title,
        content,
      })
    );
  };

  return (
    <>

        <div className="techcreate">
          <div className="form">
            <form onSubmit={submitHandler}>
              <div className="text">
                <div className="subtitle">Let's Update Models</div>
              </div>
              <div className="input-container">
                <label>Category</label>
                <input
                  id="category"
                  className="input"
                  type="text"
                  placeholder="categorygfhfg"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                />
              </div>
              <div className="input-container">
                <label>Country</label>
                <input
                  id="country"
                  className="input"
                  type="text"
                  placeholder="country"
                  value={country}
                  onChange={(e) => setCountry(e.target.value)}
                />
              </div>
              <div className="input-container">
                <label>State</label>
                <input
                  id="state"
                  className="input"
                  type="state"
                  placeholder="state"
                  value={state}
                  onChange={(e) => setState(e.target.value)}
                />
              </div>
              <div className="input-container ic2">
                <label>Address</label>
                <input
                  id="address"
                  className="input"
                  type="address"
                  placeholder="Url"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                />
              </div>
              <div className="input-container ic2">
                <label>Contact</label>
                <input
                  id="contact"
                  className="input"
                  type="text"
                  placeholder="contact"
                  value={contact}
                  onChange={(e) => setContact(e.target.value)}
                />
              </div>
              <div className="input-container ic2">
                <label>Title</label>
                <input
                  id="title"
                  className="input"
                  type="text"
                  placeholder="title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                />
              </div>

              <div className="input-container ic2">
                <label>Content</label>
                <textarea
                  id="content"
                  className="input"
                  type="textfield"
                  placeholder="Content"
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                />
              </div>

              <div className="input-container ic2">
                <label>Images</label>
                <input
                  id="image"
                  className="input"
                  type="text"
                  placeholder="image"
                  value={image}
                  onChange={(e) => setImage(e.target.value)}
                />
              </div>
              {/* {userInfo.isAdmin && (
              <div className="input-container ic2">
              <label>Approved</label>
              <input
                id="approved"
                className="input"
                type="text"
                placeholder="approved"
                value={image}
                onChange={(e) => setImage(e.target.value)}
              />
            </div>
            )} */}
              <div className="input-container ic2">
                <button className="button_input" type="submit">
                  Submit
                </button>
              </div>
            </form>
            {/* <div className="techcreate">
            {<TechCreateNewsApi/>}

          </div> */}
          </div>
        </div>

    </>
  );
};

export default AdvertiseUpdate;
