import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Cards from "../components/Cards";
import Footers from "../components/Footers";
import Banners from "../components/Banners";

import "../css_styles/TechList.css";
import { Grid, Typography } from "@mui/material";
// IMPORT FROM ACTIONS
import { listTechs } from "../actions/techActions";
import { advertiseListAction } from "../actions/advertiseActions";

import Categories from "../components/Categories";
import Advertise from "../components/Advertise";
import CelebCarousel from "../components/CelebCarousel";
import Loaders from "../components/Loader";
import AdsComponent from "../components/AdsComponent";
import ErrorMessage from "../components/ErrorMessage";


import { makeStyles } from '@mui/styles';


const useStyles = makeStyles({
  root: {
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 48,
    padding: '0 30px',
  },
  title:{
    fontSize: '16px',
  },
  gridHeader: {
    justifyContent: 'center',
    marginTop: '10px', 
    marginBottom: '10px',
    backgrounColor: 'white',
    color:'green'
  },
  date:{
    opacity:0.6,
    fontSize: '14px',
    display:'flex',
    alignItem:'center',
    textAlign:'center'

  },
  header : {
    fontSize: '20px'
  }
});


const techss = {
  title: "title",
  urlToImage:
    "https://staticg.sportskeeda.com/editor/2021/11/caca5-16370851386444-1920.jpg",
  description: "description",
  author: "author",
  content: "content",
};

const HomeScreen = () => {

  const classes = useStyles()
  const dispatch = useDispatch();

  const techList = useSelector((state) => state.techList);
  const advertiseList = useSelector((state) => state.advertiseList);

  const { error: techListError, loading: techListLoading, techs } = techList;
  const {
    error: advertiseError,
    loading: advertiseLoading,
    advertises,
  } = advertiseList;

  useEffect(() => {
    dispatch(listTechs());
    dispatch(advertiseListAction());
  }, [dispatch]);

  // const [techs, setTechs] = useState([]);
  // useEffect(() => {
  //   async function fetchTech() {
  //     const { data } = await axios.get(
  //       "http://127.0.0.1:8000/api/technology/list/"
  //     );
  //     setTechs(data);
  //   }
  //   fetchTech();
  // }, []);
  // console.log("tech", techs);

  return (
    <>
      <div className="techlist">
        <CelebCarousel celeb={techss} />
        <Banners />
        <Categories />

        {/* Content */}

        <Grid className={classes.gridHeader} container item >
        <Typography className={classes.header} color='green' variant="h3">TRENDING  NEWS</Typography>   
        </Grid>

        <Grid style={{justifyContent: 'center', alignItem:'center', display: 'flex'}} container>
          <Grid   spacing={1} item xs={12} sm={3} lg={3} md={3}></Grid>
            {
              techListLoading 
              ? <Loaders/>
              :techListError
              ?<ErrorMessage type="error" error={techListError} />
              :(
                <Grid spacing={1} item xs={12} sm={6} lg={6} md={6}>

                {techs.map((tech) => (
                  <Cards key={tech.id} tech={tech} />
                ))}
                </Grid>
              )
            }

          <Grid spacing={1} item xs={12} sm={3} lg={3} md={3}></Grid>
        </Grid>

        {/* <div className="techlist_main_news">
              <div className="techlist_main_news_news1">
                {techListLoading ? (
                  <div className="techlist_main_news1">
                    <Loaders />
                  </div>
                ) : techListError ? (
                  <ErrorMessage type="error" error={techListError}/>
                ) : ( */}

        {/* <div className="techlist_main_news2">
                {advertiseLoading ? (
                  <Loaders />
                ) : advertiseError ? (
                  <ErrorMessage type="error" error={advertiseError} />
                ) : (
                  <div className="techlist_main_ads">
                    {advertises
                      ? advertises.map((advertise) => (
                          <Advertise key={advertise.id} advertise={advertise} />
                        ))
                      : null}
                  </div>
                )}
              </div> */}
        {/* <div className="techlist_main_news_ads">
                <AdsComponent />
              </div> */}

        <Footers />
      </div>
    </>
  );
};

export default HomeScreen;
