import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate, Link, useParams } from "react-router-dom";

import {USER_UPDATE_RESET} from "../constants/userConstants"

import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";
import { 
    userDetailActions,
    userUpdateAction

 } from "../actions/userActions";

import "../css_styles/Signup.css";

const UserUpdate = () => {
    const params = useParams()
    const profile_id = params.id



  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  const navigate = useNavigate()

  const dispatch = useDispatch();

  const userDetail = useSelector((state) => state.userDetail);
  const userUpdate = useSelector((state) => state.userUpdate)


  const {
    error:   detailUserError,
    loading: detailUserLoading,
    user   : detailUser,
  } = userDetail;

  const {
      error:    updateUserError,
      loading:  updateUserLoading,
      user    : updateUser,
      success : updateUserSuccess
  } = userUpdate


  useEffect(() => {
    if(updateUserSuccess){
        dispatch({type: USER_UPDATE_RESET})
        navigate('/my-dashboard')
    } else {
        if(detailUser.id !==Number(profile_id) ){

        dispatch(userDetailActions())
        }

}
  },[ dispatch, updateUserSuccess, detailUser])

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(userDetailActions(username, email, password, password2));
  };

  return (
    <>
      <div className="signup">
        <div className="signup_main">
          <div className="signup_form">
            <form onSubmit={submitHandler}>
              <div className="signup_text">
                <div className="signup_text1">LOGIN</div>
              </div>
              <div className="signup_container">
                <label>username</label>
                <input
                  id="username"
                  className="input"
                  type="text"
                  placeholder="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div className="signup_container">
                <label>email</label>
                <input
                  id="email"
                  className="input"
                  type="email"
                  placeholder="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>

              <div className="signup_container">
                <label>password</label>
                <input
                  id="password"
                  className="input"
                  type="password"
                  placeholder="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <div className="signup_container">
                <label>confirm password</label>
                <input
                  id="password"
                  className="input"
                  type="password"
                  placeholder="confirm password"
                  value={password2}
                  onChange={(e) => setPassword2(e.target.value)}
                />
              </div>

              <div className="signup_button">
                <button className="button_input" type="submit">
                  Submit
                </button>
              </div>
            </form>
            {updateUserLoading && <Loaders />}
            {updateUserError && (
              <ErrorMessage type="error" error={updateUserError} />
            )}

          </div>
        </div>
      </div>
    </>
  );
};

export default UserUpdate;
