import { createStore, combineReducers, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import { composeWithDevTools } from 'redux-devtools-extension';
import { techListReducer, createTechReducer } from '../src/reducers/techReducers';
import { userLoginReducer } from '../src/reducers/userReducers';
import { advertiseListReducer, advertiseDetailReducer, advertiseCreateReducer, advertiseUpdateReducer, advertiseDeleteReducer // CELEBRITY PORTION
, celebrityListReducer, celebrityDetailReducer, celebrityCreateReducer, celebrityUpdateReducer, celebrityDeleteReducer // EVENT PORTION
, eventListReducer, eventDetailReducer, eventCreateReducer, eventUpdateReducer, eventDeleteReducer } from '../src/reducers/productivityReducers';
const reducer = combineReducers({
  techList: techListReducer,
  techCreate: createTechReducer,
  userLogin: userLoginReducer,
  advertiseList: advertiseListReducer,
  advertiseDetail: advertiseDetailReducer,
  advertiseCreate: advertiseCreateReducer,
  advertiseUpdate: advertiseUpdateReducer,
  advertiseDelete: advertiseDeleteReducer,
  // CELEBRITY PORTION
  celebrityList: celebrityListReducer,
  celebrityDetail: celebrityDetailReducer,
  advertiseCreate: advertiseCreateReducer,
  advertiseUpdate: advertiseUpdateReducer,
  advertiseDelete: advertiseDeleteReducer,
  // EVENT PORTION
  eventList: eventListReducer,
  eventDetail: eventDetailReducer,
  eventUpdate: eventUpdateReducer,
  eventDelete: eventDeleteReducer,
  eventCreate: advertiseCreateReducer
});
const userInfoFromStorage = localStorage.getItem('userLogin') ? JSON.parse(localStorage.getItem('userLogin')) : null;
const initialState = {
  userLogin: {
    userInfo: userInfoFromStorage
  }
};
const middleware = [thunk];
const store = createStore(reducer, initialState, composeWithDevTools(applyMiddleware(...middleware)));
export default store;
