import React from "react";
import "../css_styles/cards.css";
import { Link } from "react-router-dom";
import {Typography} from '@mui/material';
import { makeStyles } from '@mui/styles';

import CalendarTodayIcon from '@mui/icons-material/CalendarToday';

const useStyles = makeStyles({
  root: {
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 48,
    padding: '0 30px',
  },
  title:{
    fontSize: '16px',
  },
  date:{
    opacity:0.6,
    fontSize: '14px',

  }
});

const Cards = ({ tech }) => {

  const classes = useStyles();
  return (

    <Link className="text-link" to="/news-detail">
      <div className="cards">
        <div className="card-image">

        <img src={tech.urlToImage} alt="" />
        </div>
        <div  className="card-text">
          <div className="card-date">
          <Typography className={classes.date} variant="h3"><CalendarTodayIcon/>Date {tech.publishedAt}</Typography>
          </div>

        <Typography className={classes.title} variant="h3">{tech.title}</Typography>        
        </div>
      </div>
    </Link>
  );
};

export default Cards;
