import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import NewsCards from "../components/newsCards";
import Footers from "../components/Footers";
import Banners from "../components/Banners";
import { useLocation } from "react-router-dom";

import "../css_styles/TechList.css";
// IMPORT FROM ACTIONS
import { listTechs } from "../actions/techActions";
import { advertiseListAction } from "../actions/advertiseActions";

import Categories from "../components/Categories";
import CelebCarousel from "../components/CelebCarousel";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";
// import CategoryCarousel from "../components/CategoryCarousel";
import SearchBox from "../components/SearchBox";
import Paginate from "../components/Pagination";

import { Grid, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles({
  root: {
    background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)",
    border: 0,
    borderRadius: 3,
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
    color: "white",
    height: 48,
    padding: "0 30px",
  },
  gridContent: {
    justifyContent: "center",
    alignItem: "center",
    display: "flex",
    paddingBottom: "20px",
    backgrounColor: "white",
  },
  title: {
    fontSize: "16px",
  },
  gridHeader: {
    justifyContent: "center",
    marginTop: "10px",
    marginBottom: "10px",
    backgrounColor: "white",
    color: "green",
  },
  date: {
    opacity: 0.6,
    fontSize: "14px",
    display: "flex",
    alignItem: "center",
    textAlign: "center",
  },
  header: {
    fontSize: "20px",
  },
});

const techss = {
  title: "title",
  urlToImage:
    "https://staticg.sportskeeda.com/editor/2021/11/caca5-16370851386444-1920.jpg",
  description: "description",
  author: "author",
  content: "content",
};

const HomeScreen = () => {
  const location = useLocation();
  let keyword = location.search;

  // const pathname = window.location.pathname

  const classes = useStyles();
  const dispatch = useDispatch();

  const techList = useSelector((state) => state.techList);

  const { error: techListError, loading: techListLoading, techs, pages, page } = techList;

  useEffect(() => {
    dispatch(listTechs(keyword));
    dispatch(advertiseListAction());
  }, [dispatch, keyword]);

  return (
    <>
      <div className="techlist">
        <CelebCarousel celeb={techss} />
        <Banners />
        <Categories />

        <SearchBox />
        {/* Content */}

        <Grid className={classes.gridHeader} container item>
          <Typography className={classes.header} color="green" variant="h3">
            TRENDING NEWS
          </Typography>
        </Grid>

        <Grid spacing={2} className={classes.gridContent} container>
          <Grid spacing={1} item xs={12} sm={3} lg={3} md={3}>
            Hi Loverlj lask jflks jfkslajfkl sdajfk sjsdljgk ldjgksjdk fgjs
            kldjgl dfjl jfkls ajfkls
          </Grid>
          {techListLoading ? (
            <Loaders />
          ) : techListError ? (
            <ErrorMessage type="error" error={techListError} />
          ) : (
            <Grid spacing={1} item xs={12} sm={6} lg={6} md={6}>
              {techs.map((tech) => (
                <NewsCards key={tech.id} new={tech} model="model"/>
              ))}
            </Grid>
          )}

          <Grid spacing={1} item xs={12} sm={3} lg={3} md={3}>
            jal;fjdsj dfksjflkdjk fhkjasdjg sgjslkgjk ldjfkjdl fjdhfkjash
            fijksdhf
          </Grid>
        </Grid>
        <Paginate keyword={keyword} page={page} pages={pages}/>

        {/* <CategoryCarousel celeb={techss} /> */}

        <Footers />
      </div>
    </>
  );
};

export default HomeScreen;
