import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {createTechs} from '../actions/techActions'

import "../css_styles/TechCreate.css";

const LoginScreens = () => {
  const [author, setAuthor] = useState("");
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [url, setUrl] = useState("");
  const [urlToImage, setUrlToImage] = useState("");
  const [publishedAt, setPublishedAt] = useState("");
  const [content, setContent] = useState("");

  const dispatch = useDispatch()
  // user = user,
  // author = data['author'],
  // title = data['title'],
  // description = data['description'],
  // url= data['url'],
  // urlToImage= data['urlToImage'],
  // publishedAt = data['publishedAt'],
  // content = data['content']

  const submitHandler = (e) =>  {
    e.preventDefault();
    dispatch(createTechs({
      author,
      title,
      description,
      url,
      urlToImage,
      publishedAt,
      content
    }))
    console.log('clicked', author)
  }

  return (
    <>
      <div className="techcreate">
        <div className="form">
          <form onSubmit={submitHandler}>
            <div className="text">
              <div className="title">Welcome Admin</div>
              <div className="subtitle">Let's Upload Tech Stories</div>
            </div>
            <div className="input-container">
              <label>Author</label>
              <input
                id="author"
                className="input"
                type="text"
                placeholder="Author"
                value={author}
                onChange={(e) => setAuthor(e.target.value)}
              />
            </div>
            <div className="input-container ic2">
              <label>Title</label>
              <input
                id="title"
                className="input"
                type="text"
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>


            <div className="input-container ic2">
              <button className="input" type="submit">
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default LoginScreens
