import {
    ADVERTISE_LIST_REQUEST,
    ADVERTISE_LIST_SUCCESS,
    ADVERTISE_LIST_FAIL,

    ADVERTISE_DETAIL_REQUEST,
    ADVERTISE_DETAIL_SUCCESS,
    ADVERTISE_DETAIL_FAIL,
} from '../constants/productivityConstants'

import axios from 'axios'

// ADVERTISEMENT PORTION

export const advertiseList = () => async (dispatch) => {
    try{
        dispatch({ type: ADVERTISE_LIST_REQUEST });
        const { data } = await axios.get(
            "http://127.0.0.1:8000/api/advertisement/list/"
        );
        dispatch({
            type: ADVERTISE_LIST_SUCCESS,
            payload: data,
        })
    } catch (error) {
        dispatch({
            type: ADVERTISE_LIST_FAIL,
            payload:
              error.response && error.response.data.detail
                ? error.response.data.detail
                : error.message,
    })
    }
}



export const advertiseDetail = ({id}) => async (dispatch) => {
    try{
        dispatch({ type: ADVERTISE_DETAIL_REQUEST });
        const { data } = await axios.get(
            `http://127.0.0.1:8000/api/advertisement/list/${id}`
        );
        dispatch({
            type: ADVERTISE_LIST_SUCCESS,
            payload: data,
        })
    } catch (error) {
        dispatch({
            type: ADVERTISE_LIST_FAIL,
            payload:
              error.response && error.response.data.detail
                ? error.response.data.detail
                : error.message,
    })
    }
}
