import ErrorMessage from "../components/ErrorMessage";

import "../css_styles/Advertise.css";
const AdvertiseCreate = () => {

  const history = useNavigate()
  const dispatch =  useDispatch()

  const advertiseCreate = useSelector((state) => state.advertiseCreate);

  const {error: advertiseError, loading:advertiseLoading, advertise, success} = advertiseCreate




  useEffect(() => {
    dispatch(advertiseCreateAction())
    if (success) {

      history('/user-post-create')
    }

  }, [dispatch, history])
  return (
    <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center' , flexDirection: 'column'}}>
      {
      advertiseError
        ? <ErrorMessage type="error" error={ErrorMessage} />
        : (
          <div>
          Creating....
          <Loaders />
          </div>

        )
      }
    </div>
  );
};

export default AdvertiseCreate;
