import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {useParams, useNavigate } from "react-router-dom";

// IMPORT COMPONENT

// import TechCreateNewsApi from "../admin-screen/TechCreateNewsApi";
import { eventDetailAction } from "../actions/advertiseActions";
import { eventUpdateAction } from "../actions/advertiseActions";
import { EVENT_UPDATE_RESET } from "../constants/productivityConstants";

import "../css_styles/TechCreate.css";

const EventUpdate = () => {
  const params = useParams();
  const event_id = params.id;
  const navigate = useNavigate();



  const [category, setCategory] = useState("");
  const [country, setCountry] = useState("");
  const [state, setState] = useState("");
  const [address, setAddress] = useState("");
  const [contact, setContact] = useState("");
  const [image, setImage] = useState("");
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [isApproved, setIsApproved] = useState(false);

  const dispatch = useDispatch();
  const eventDetail = useSelector((state) => state.eventDetail);
  const {
    error:     detailEventError,
    loading:   detailEventLoading,
    event: detailEvent,
  } = eventDetail;

  const eventUpdate = useSelector((state) => state.eventUpdate);
  const {
    error:     updateEventError,
    loading:   updateEventLoading,
    success:   updateEventSuccess,
    event: updateEvent,
  } = eventUpdate;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    if (updateEventSuccess) {
      dispatch({ type:EVENT_UPDATE_RESET });
      navigate("/event-create");
    } else {
      if (
        !detailEvent.title ||
        detailEvent.id !== Number(event_id)
      ) {
        dispatch(eventDetailAction(event_id));
      } else {
        setCategory(detailEvent.category);
        setCountry(detailEvent.country);
        setState(detailEvent.state);
        setAddress(detailEvent.address);
        setContact(detailEvent.contact);
        setImage(detailEvent.image);
        setTitle(detailEvent.title);
        setContent(detailEvent.content);
      }
    }
  }, [dispatch, event_id, detailEvent,updateEventSuccess, updateEvent]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(
      eventUpdateAction({
        id: event_id,
        category,
        country,
        state,
        address,
        contact,
        image,
        title,
        content,
        isApproved
      })
    );
  };

  return (
    <>

        <div className="techcreate">
          <div className="form">
            <form onSubmit={submitHandler}>
              <div className="text">
                <div className="subtitle">Let's Update Models</div>
              </div>
              <div className="input-container">
                <label>Category</label>
                <input
                  id="category"
                  className="input"
                  type="text"
                  placeholder="categorygfhfg"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                />
              </div>
              <div className="input-container">
                <label>Country</label>
                <input
                  id="country"
                  className="input"
                  type="text"
                  placeholder="country"
                  value={country}
                  onChange={(e) => setCountry(e.target.value)}
                />
              </div>
              <div className="input-container">
                <label>State</label>
                <input
                  id="state"
                  className="input"
                  type="state"
                  placeholder="state"
                  value={state}
                  onChange={(e) => setState(e.target.value)}
                />
              </div>
              <div className="input-container ic2">
                <label>Address</label>
                <input
                  id="address"
                  className="input"
                  type="address"
                  placeholder="Url"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                />
              </div>
              <div className="input-container ic2">
                <label>Contact</label>
                <input
                  id="contact"
                  className="input"
                  type="text"
                  placeholder="contact"
                  value={contact}
                  onChange={(e) => setContact(e.target.value)}
                />
              </div>
              <div className="input-container ic2">
                <label>Title</label>
                <input
                  id="title"
                  className="input"
                  type="text"
                  placeholder="title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                />
              </div>

              <div className="input-container ic2">
                <label>Content</label>
                <textarea
                  id="content"
                  className="input"
                  type="textfield"
                  placeholder="Content"
                  value={content}
                  onChange={(e) => setContent(e.target.value)}
                />
              </div>

              <div className="input-container ic2">
                <label>Images</label>
                <input
                  id="image"
                  className="input"
                  type="text"
                  placeholder="image"
                  value={image}
                  onChange={(e) => setImage(e.target.value)}
                />
              </div>
              {userInfo.isAdmin && (
              <div className="input-container ic2">
              <label>Approved</label>
              <select>

              </select>
              {/* <input
                id="approved"
                className="input"
                type="text"
                placeholder="approved"
                value={isApproved}
                onChange={(e) => setIsApproved(e.target.value)}
              /> */}
            </div>
            )}
              <div className="input-container ic2">
                <button className="button_input" type="submit">
                  Submit
                </button>
              </div>
            </form>
            {/* <div className="techcreate">
            {<TechCreateNewsApi/>}

          </div> */}
          </div>
        </div>

    </>
  );
};

export default EventUpdate;
