import React, { useEffect } from "react";
import "../css_styles/AdminDashboard.css";
import AdminSidebar from "../components/AdminSidebar";
import PerformanceChart from "../components/PerformanceChart";
import Table from "../components/Table";
import UserTable from "../components/UserTable";
import {useNavigate} from "react-router-dom"

import { useSelector, useDispatch } from "react-redux";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

import { userListActions } from "../actions/userActions";
import "../css_styles/UserTable.css";

// CONSTANT IMPORT
import {
  ADVERTISE_CREATE_RESET,
  ADVERTISE_DELETE_RESET,
} from "../constants/productivityConstants";

// ACTION IMPORT
import {
  advertiseListAction,
  advertiseCreateAction,
  advertiseDeleteAction,
} from "../actions/advertiseActions";



const AdminDashboard = ({ tech }) => {

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const navigate = useNavigate()
  const dispatch = useDispatch();

  const userList = useSelector((state) => state.userList);
  const { error: listUserError, loading: listUserLoading, users } = userList;



  // ADVERTISE CREATE

  const advertiseCreate = useSelector((state) => state.advertiseCreate);
  const {
    error: advertiseCreateError,
    loading: advertiseCreateLoading,
    advertise: advertise,
    success: advertiseCreateSuccess,
  } = advertiseCreate;


   // ADVERTISE LIST
   const advertiseList = useSelector((state) => state.advertiseList);
   const {
     error: listAdvertiseError,
     loading: listAdvertiseLoading,
     advertises: listAdvertise,
   } = advertiseList;

   // ADVERTISE DELETE
   const advertiseDelete = useSelector((state) => state.advertiseDelete);
   const {
     error: deleteAdvertiseError,
     loading: deleteAdvertiseLoading,
     advertise: deleteAdvertise,
     success: deleteAdvertiseSuccess,
   } = advertiseDelete;




  useEffect(() => {
    if (!userInfo){
      navigate("/")

    } 
    dispatch(userListActions());

    dispatch(advertiseListAction());
    dispatch({ type: ADVERTISE_CREATE_RESET });
    dispatch({ type: ADVERTISE_DELETE_RESET });

    if (advertiseCreateSuccess) {
      navigate(`/user-post-create/${advertise.id}`);
    }

  }, [dispatch, userInfo, advertiseCreateSuccess, deleteAdvertiseSuccess]);


  const createProductHandler = () => {
    dispatch(advertiseCreateAction());
  };

  // DELETE ADVERTISE HANDLER
  const advertiseDeleteHandler = (id) => {
    if (window.confirm("Are You Sure You Want To Delete This Items")) {
      dispatch(advertiseDeleteAction(id));
    }
  };

  return (
    <div className="admin">
      <div className="admin_top">Admin Headers</div>

      <div className="admin_main">
        <div className="admin_sidebar">
          <AdminSidebar />
        </div>
        <div className="admin_body">
          <PerformanceChart />
          <div className="admin_status">
            <div className="admin_status1">
              <div className="admin_status1_items">Icon</div>
              <div className="admin_status1_items">
                Visitors
                <br />
                1235
              </div>
            </div>
            <div className="admin_status1">
              <div className="admin_status1_items">Icon</div>
              <div className="admin_status1_items">
                Page Views
                <br />
                1235
              </div>
            </div>
          </div>
          <div className="users_post">
            {/* <h2 className="users_post_text">Local News</h2>
            <Table cat={"Local News"} />

            <h2 className="users_post_text">Jobs</h2> */}
            {/* <Table cat={"Jobs"}/>
            <h2 className="users_post_text">Advertise</h2>
            <Table cat={"Advertise"}/>
            <h2 className="users_post_text">Shops</h2>
            <Table  cat={"Shops"}/>
            <h2 className="users_post_text">Business</h2>
            <Table  cat={"Business"}/>
            <h2 className="users_post_text">Celebrities</h2>
            <Table  cat={"Celebrities"}/>
            <h2 className="users_post_text">Hotels</h2>
            <Table  cat={"Hotels"}/>
            <h2 className="users_post_text">Tourisms</h2>
            <Table  cat={"Tourisms"}/> */}
          </div>
        </div>

        {listUserError && <ErrorMessage type="error" error={listUserError} />}
        {listUserLoading && <Loaders />}
        <div className="users">
          <div className="user_table">
            <table className="user_scroll">
              <thead>
                <tr className="user_header">
                  <th>user</th>
                  <th>username</th>
                  <th>password</th>
                </tr>
              </thead>
              <tbody>
                {users &&
                  users.map((user) => (
                    <tr>
                      <td>{user.username}</td>
                      <td>{user.email}</td>
                      <td>{user.password}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
