import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import "../css_styles/Tourisms.css";
import Jobs from "../components/Jobs";
import Footers from "../components/Footers";
import Banners from "../components/Banners";
import Categories from "../components/Categories";
import CelebCarousel from "../components/CelebCarousel";

import { jobListAction } from "../actions/advertiseActions2";

const celeb = {
  title: "title",
  urlToImage:
    "https://staticg.sportskeeda.com/editor/2021/11/caca5-16370851386444-1920.jpg",
  description: "description",
  author: "author",
  content: "content",
};
const JobsScreen = () => {
  const dispatch = useDispatch();

  const jobList = useSelector((state) => state.jobList);
  const {
    error: listJobError,
    loading: listJobLoading,
    jobs: listJob,
  } = jobList;

  useEffect(() => {
    dispatch(jobListAction());
  }, [dispatch]);

  return (
    <div>
      <CelebCarousel celeb={celeb} />
      <Banners />
      <Categories />
      <div className="tourist_screen">
        <div className="tourist_event">hI</div>
        <div className="tourist_main">
          {listJob.map((job) => (
            <Jobs key={job.id} job={job} />
          ))}
        </div>
        <div className="tourist_event">hI</div>
      </div>
      <Footers />
    </div>
  );
};

export default JobsScreen;
