import {
  TECH_LIST_REQUEST,
  TECH_LIST_SUCCESS,
  TECH_LIST_FAIL,

  CREATE_TECH_REQUEST,
  CREATE_TECH_SUCCESS,
  CREATE_TECH_FAIL,
} from "../constants/techConstants";

import axios from 'axios'

export const listTechs =() => async (dispatch) =>  {
    try {
        dispatch({ type: TECH_LIST_REQUEST})
        const {data } = await axios.get('http://127.0.0.1:8000/api/technology/list/')

        dispatch({
            type: TECH_LIST_SUCCESS,
            payload:data
        })

    } catch (error) {
        dispatch({
            type: TECH_LIST_FAIL,
            payload: error.response && error.response.data.detail
                ? error.response.data.detail
                :error.message,
        })
    }
}

export const createTechs = (techs) => async (dispatch) => {
    try {
        dispatch({type: CREATE_TECH_REQUEST})
        const config = {
            headers: {
                'Content-type': 'application/json'
            }
        }
        const {data} = await axios.post(
            'http://127.0.0.1:8000/api/technology/create/',
            techs,
            config
            )
        dispatch({type: CREATE_TECH_SUCCESS, payload: data})

    } catch (error) {
        dispatch({
            type: TECH_LIST_FAIL,
            payload: error.response && error.response.data.detail
                ? error.response.data.detail
                :error.message,
        })
}
}
