import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { userLoginActions } from "../actions/userActions";
import {Link} from "react-router-dom"
import {useNavigate} from "react-router-dom"

import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

import "../css_styles/LoginScreen.css";

const LoginScreens = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const userLogin = useSelector((state) => state.userLogin)

  const {
    error:loginUserError,
    loading:loginUserLoading,
    userInfo
  } = userLogin


  useEffect(() => {
    if(userInfo){
      navigate("/")
    }
  })

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(userLoginActions(username, password));
  };

  return (
    <>
      <div className="login">
        <div className="login_main">
          <div className="login_form">
            {loginUserLoading && <Loaders/>}
            {loginUserError && <ErrorMessage type="error" error={loginUserError} />}
            <form onSubmit={submitHandler}>
              <div className="login_text">
                <div className="login_text1">LOGIN</div>
              </div>
              <div className="login_container">
                <label>username</label>
                <input
                  id="username"
                  className="input"
                  type="text"
                  placeholder="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div className="login_container">
                <label>password</label>
                <input
                  id="password"
                  className="input"
                  type="password"
                  placeholder="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <div className="login_button">
                <button className="button_input" type="submit">
                  Submit
                </button>
              </div>
            </form>
            <div className="signup_login">
              No Account ? 
              <Link to="/signup">
              <h2>SignUp</h2>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginScreens;
