import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Footers from "../components/Footers";
import "../css_styles/DetailProductivity.css";
import ProductivityDetail from "../components/ProductivityDetail";
import { useParams } from "react-router-dom";

import { hotelDetailAction } from "../actions/advertiseActions2";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

const Detail = ({ match, history, location }) => {
    const params = useParams();
    const id = params.id;

    const dispatch = useDispatch();
    const hotelDetail = useSelector((state) => state.hotelDetail);

    const {
      error:   detailHotelError,
      loading: detailHotelLoading,
      hotel:   detailHotel,
    } = hotelDetail;

    useEffect(() => {
      dispatch(hotelDetailAction(id));
    }, [dispatch]);

    return (
      <div>
        <div className="productivity_main">
          <div className="productivity_advertise"></div>
          {detailHotelLoading ? (
            <Loaders />
          ) : detailHotelError ? (
            <ErrorMessage type="error" error={detailHotelError} />
          ) : (
            <div className="productivity_cards">
              <ProductivityDetail tech={detailHotel} model={"Hotel"} />
            </div>
          )}
          <div className="productivity_advertise">
            {/* <Advertise tech={techs} /> */}
          </div>
        </div>

        <Footers />
      </div>
    );
  };

export default JobDetail;
