import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate, Link } from "react-router-dom";

import {USER_REGISTER_RESET} from "../constants/userConstants"

import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";
import { userRegisterActions } from "../actions/userActions";
import {
  Grid,
  Paper,
  Typography,
  Avatar,
  TextField,
  Button,
} from "@mui/material";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import LockOpenIcon from '@mui/icons-material/LockOpen';


import "../css_styles/Signup.css";

const Register = () => {
  const avatarStyle = {backgroundColor:'green'}
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  const navigate = useNavigate()

  const dispatch = useDispatch();
  const userRegister = useSelector((state) => state.userRegister);

  const {
    error: registerUserError,
    loading: registerUserLoading,
    success: registerUserSuccess,
  } = userRegister;

  useEffect(() => {
    if(registerUserSuccess){
      dispatch({type: USER_REGISTER_RESET})
      navigate('/login')
    }
  },[ dispatch, registerUserSuccess])

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(userRegisterActions(username, email, password, password2));
  };

  return (
    <>
      <Grid>
        <Paper className="paperHeader" elevation={10}>
          <Grid className="gridHeader">
            <Grid className="gridIcon" align="center">
              {registerUserLoading && <Loaders />}
              {registerUserError && (
                <ErrorMessage type="error" error={registerUserError} />
              )}
              <Avatar style={avatarStyle}>
                <LockOpenIcon />
              </Avatar>
              <h2>sign Up</h2>
              <Typography className="text">Please fill this form to create an account.</Typography>
            </Grid>

            <TextField
              id="username"
              className="input"
              type="text"
              placeholder="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              fullWidth
            />
            <TextField
              id="password"
              className="input"
              type="password"
              placeholder="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              fullWidth
              required
            />
            <FormControlLabel
              label="Remember me"
              control={<Checkbox name="checkedB" color="success" />}
            />

            <Button
            className="button"
              onClick={submitHandler}
              type="submit"
              color="success"
              variant="contained"
              fullWidth
            >
              Sign IN
            </Button>

            {/* <Link className="linkText" to="/forgotPassword">
              <Typography>Forgot password ?</Typography>
            </Link> */}
            <Typography className="text">No Account ?</Typography>
            <Link className="linkText" to="/register">
              <Typography className="text">sign up</Typography>

            </Link>
          </Grid>
        </Paper>
      </Grid>
      {/* <div className="signup">
        <div className="signup_main">
          <div className="signup_form">
            <form onSubmit={submitHandler}>
              <div className="signup_text">
                <div className="signup_text1">LOGIN</div>
              </div>
              <div className="signup_container">
                <label>username</label>
                <input
                  id="username"
                  className="input"
                  type="text"
                  placeholder="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div className="signup_container">
                <label>email</label>
                <input
                  id="email"
                  className="input"
                  type="email"
                  placeholder="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>

              <div className="signup_container">
                <label>password</label>
                <input
                  id="password"
                  className="input"
                  type="password"
                  placeholder="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <div className="signup_container">
                <label>confirm password</label>
                <input
                  id="password"
                  className="input"
                  type="password"
                  placeholder="confirm password"
                  value={password2}
                  onChange={(e) => setPassword2(e.target.value)}
                />
              </div>

              <div className="signup_button">
