import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { advertiseCreateAction } from "../actions/advertiseActions";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";
import { ADVERTISE_CREATE_RESET } from "../constants/productivityConstants";

import "../css_styles/Advertise.css";
const AdvertiseCreate = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const params = useParams()
  const {id}=params
  console.log("params", id)

  const advertiseCreate = useSelector((state) => state.advertiseCreate);

  const {
    error: advertiseError,
    loading: advertiseLoading,
    advertise,
    success,
  } = advertiseCreate;

  useEffect(() => {
    dispatch(advertiseCreateAction());
    if (success) {
      dispatch({type: ADVERTISE_CREATE_RESET})
      console.log("before navigate")
      navigate(`/user-post-create/${advertise.id}`);
      console.log("after navigate")
    }
    // navigate(`/user-post-create/${advertise.id}`);
  }, [dispatch]);
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      {advertiseError ? (
        <ErrorMessage type="error" error={advertiseError} />
      ) : (
        <div>
          Creating....
          <Loaders />
        </div>
      )}
    </div>
  );
};

export default AdvertiseCreate;
