      fontWeight: '900'
   },
  },
  gridHeader: {
    justifyContent: 'center',
    marginTop: '10px', 
    marginBottom: '10px',
    backgrounColor: 'white',
    color:'green',
    '&:hover': {
      background: 'white',
   },

  },

  date:{
    opacity:0.6,
    fontSize: '14px',
    display:'flex',
    alignItem:'center',
    textAlign:'center'

  },
  header : {
    fontSize: '20px'
  }
});

const Banners = () => {
  const classes = useStyles()
  return (
    <div className="banner">
      <h2 className="banner_text">I was motivated by Your Beauty</h2>
      <div className="banner_author">
        <h2 className="author">--darklover</h2>
      </div>
    </div>
  );
};

export default Banners;
