import React, { useState, useEffect } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { createTechs } from "../actions/techActions";

import "../css_styles/TechCreate.css";

const TechCreateNewsApi = () => {
  const [author, setAuthor] = useState("");
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [url, setUrl] = useState("");
  const [urlToImage, setUrlToImage] = useState("");
  const [publishedAt, setPublishedAt] = useState("");
  const [content, setContent] = useState("");

  const [techs, setTechs] = useState([]);
  const newsApiKey = "d049a308e4634c8b8a28ce3b4b3059be";
  const dispatch = useDispatch();

  const submitHandler = (tech) => {
    dispatch(
      createTechs({
        author: tech.author,
        title: tech.title,
        description: tech.description,
        url: tech.url,
        urlToImage: tech.urlToImage,
        publishedAt: tech.publishedAt,
        content: tech.content,
      })
    );
    console.log("clicked", author);
  };

  //   FETCH THE DATA
  const techNewsApi = () => async () => {
    console.log("Fetching api");

    const { data } = await axios.get(
      `https://newsapi.org/v2/top-headlines?country=in&category=technology&apiKey=${newsApiKey}`
    );

    console.log("data", data);
    console.log("techs1", techs);
    setTechs(data.articles);
    console.log("techs2", techs);

    techs &&
      techs.map((tech) => {
        return (
          console.log("hi", tech.author),
          setAuthor(tech.author),
          setTitle(tech.title),
          setDescription(tech.description),
          setUrl(tech.url),
          setUrlToImage(tech.urlToImage),
          setPublishedAt(tech.publishedAt),
          setContent(tech.content),
          console.log("mapped author", author),
          // submitHandler(),

          console.log("inside map last line"),
          dispatch(
            createTechs({
              author: tech.author,
              title: tech.title,
              description: tech.description,
              url: tech.url,
              urlToImage: tech.urlToImage,
              publishedAt: tech.publishedAt,
              content: tech.content,
            })
          )
        );
      });
  };

  return (
    <>
      <div className="techcreate">
        <button onClick={techNewsApi()}>click</button>
        <div>{techs && techs.map((tech) => <h2>{tech.title}</h2>)}</div>
      </div>
    </>
  );
};

export default TechCreateNewsApi;
