import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Footers from "../components/Footers";
import "../css_styles/DetailProductivity.css";
import ProductivityDetail from "../components/ProductivityDetail";
import { useParams } from "react-router-dom";

import { jobDetailAction } from "../actions/advertiseActions2";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

const JobDetail = ({ match, history, location }) => {
    const params = useParams();
    const id = params.id;

    const dispatch = useDispatch();
    const jobDetail = useSelector((state) => state.jobDetail);

    const {
      error:   detailJobError,
      loading: detailJobLoading,
      job :    detailJob,
    } = jobDetail;

    useEffect(() => {
      dispatch(jobDetailAction(id));
    }, [dispatch]);

    return (
      <div>
        <div className="productivity_main">
          <div className="productivity_advertise"></div>
          {detailJobLoading ? (
            <Loaders />
          ) : detailJobError ? (
            <ErrorMessage type="error" error={detailJobError} />
          ) : (
            <div className="productivity_cards">
              <ProductivityDetail tech={detailJob} model={"Job"} />
            </div>
          )}
          <div className="productivity_advertise">
            {/* <Advertise tech={techs} /> */}
          </div>
        </div>

        <Footers />
      </div>
    );
  };

export default JobDetail;
