import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";

import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";
import { userRegisterActions } from "../actions/userActions";

import "../css_styles/Signup.css";

const Register = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");

  const dispatch = useDispatch();
  const userRegister = useSelector((state) => state.userRegister);

  const {
    error: registerUserError,
    loading: registerUserLoading,
    success: registerUserSuccess,
  } = userRegister;

  useEffect(() => {
    if(registerUserSuccess){

    }
  })

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(userRegisterActions(username, email, password, password2));
  };

  return (
    <>
      <div className="signup">
        <div className="signup_main">
          <div className="signup_form">
            <form onSubmit={submitHandler}>
              <div className="signup_text">
                <div className="signup_text1">LOGIN</div>
              </div>
              <div className="signup_container">
                <label>username</label>
                <input
                  id="username"
                  className="input"
                  type="text"
                  placeholder="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
              <div className="signup_container">
                <label>email</label>
                <input
                  id="email"
                  className="input"
                  type="email"
                  placeholder="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>

              <div className="signup_container">
                <label>password</label>
                <input
                  id="password"
                  className="input"
                  type="password"
                  placeholder="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <div className="signup_container">
                <label>confirm password</label>
                <input
                  id="password"
                  className="input"
                  type="password"
                  placeholder="confirm password"
                  value={password2}
                  onChange={(e) => setPassword2(e.target.value)}
                />
              </div>

              <div className="signup_button">
                <button className="button_input" type="submit">
                  Submit
                </button>
              </div>
            </form>
            {registerUserLoading && <Loaders />}
            {registerUserError && (
              <ErrorMessage type="error" error={registerUserError} />
            )}
            <div className="signup_login">
              Allready have an account ?<h2>Sign In</h2>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Register;
