import React, { useEffect } from "react";
import { useNavigate, useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

// COMPONENT IMPORT
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

// CSS IMPORT
import "../css_styles/Advertise.css";

// CONSTANT IMPORT
import {
  EVENT_CREATE_RESET,
  EVENT_DELETE_RESET,
} from "../constants/productivityConstants";

// ACTION IMPORT
import {
  eventListAction,
  eventCreateAction,
  eventDeleteAction,
} from "../actions/advertiseActions";



const EventCreate = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const params = useParams();
  const { id } = params;
  console.log("params", id);


  // CELEBRITIES CREATE

  const eventCreate = useSelector((state) => state.eventCreate);
  const {
    error:     createEventError,
    loading:   createEventLoading,
    celebrity: createEvent,
    success: createEventSuccess,
  } = eventCreate;

  // CELEBRITIES LIST
  const eventList = useSelector((state) => state.eventList);
  const {
    error:      listEventError,
    loading:    listEventLoading,
    celebrities:listEvent,
  } = eventList;

  // CELEBRITIES DELETE
  const eventDelete = useSelector((state) => state.eventDelete);
  const {
    error:     deleteEventError,
    loading:   deleteEventLoading,
    celebrity: deleteEvent,
    success:   deleteEventSuccess,
  } = eventDelete;


  // USEEFFECT

  useEffect(() => {
    dispatch(eventListAction());
    dispatch({ type: EVENT_CREATE_RESET });
    dispatch({ type: EVENT_DELETE_RESET });

    if (createEventSuccess) {
      navigate(`/event-update/${createEvent.id}`);
    }
  }, [dispatch, createEventSuccess, deleteEventSuccess]);


  // CREATE ADVERTISE HANDLER
  const createProductHandler = () => {
    dispatch(eventCreateAction());
  };

  // DELETE ADVERTISE HANDLER
  const celebrityDeleteHandler = (id) => {
    if (window.confirm("Are You Sure You Want To Delete This Items")) {
      dispatch(eventDeleteAction(id));
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        margin: "50px",
      }}
    >
      <div>
        {createEventError ? (
          <ErrorMessage type="error" error={createEventError} />
        ) : createEventError ? (
          <Loaders />
        ) : (
          <button onClick={createProductHandler}>Create Celebrity Post</button>
        )}
      </div>

      {listEventLoading ? (
        <Loaders />
      ) : listEventError ? (
        <ErrorMessage type="error" error={listEventError} />
      ) : (
        <div className="post_table">
          {deleteEventError && (
            <ErrorMessage type="error" error={deleteEventError} />
          )}
          <table className="post_scroll">
            <thead>
              <tr className="post_header">
                <th>id</th>
                <th>title</th>
                <th>isApproved</th>
                <th>edit</th>
                <th>delete</th>
              </tr>
            </thead>
            <tbody>
              {listEvent.map((celebrity) => (
                <tr>
                  <td>{celebrity.id}</td>
                  <td>{celebrity.title}</td>
                  <td>{celebrity.isApproved ? "Yes" : "No"}</td>
                  <Link to={`/celebrity-update/${celebrity.id}`}>
                    <td>edit</td>
                  </Link>
                  {deleteCelebrityLoading ? (
                    <Loaders />
                  ) : (
                    <td onClick={() => celebrityDeleteHandler(celebrity.id)}>
                      Delete
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default EventCreate;
