import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Footers from "../components/Footers";
import "../css_styles/DetailProductivity.css";
import ProductivityDetail from "../components/ProductivityDetail";
import { useLocation, useParams } from "react-router-dom";

import { advertiseDetailAction } from "../actions/advertiseActions";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

const AdvertiseDetail = ({ match, history, location }) => {
  const params = useParams();
  const id = params.id;

  const dispatch = useDispatch();
  const advertiseDetail = useSelector((state) => state.advertiseDetail);

  const {
    error: advertiseError,
    loading: advertiseLoading,
    advertise,
  } = advertiseDetail;

  useEffect(() => {
    dispatch(advertiseDetailAction(id));
  }, [dispatch]);

  return (
    <div>
      <div className="productivity_main">
        <div className="productivity_advertise"></div>
        {advertiseLoading ? (
          <Loaders />
        ) : advertiseError ? (
          <ErrorMessage type="error" error={advertiseError} />
        ) : (
          <div className="productivity_cards">
            <ProductivityDetail tech={advertise} model={"Advertisement"} />
          </div>
        )}
        <div className="productivity_advertise">
          {/* <Advertise tech={techs} /> */}
        </div>
      </div>

      <Footers />
    </div>
  );
};

export default AdvertiseDetail;
