import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Footers from "../components/Footers";
import "../css_styles/DetailProductivity.css";
import ProductivityDetail from "../components/ProductivityDetail";
import { useParams } from "react-router-dom";

import { eventDetailAction } from "../actions/advertiseActions";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

const AdvertiseDetail = ({ match, history, location }) => {
    const params = useParams();
    const id = params.id;


    const dispatch = useDispatch();
    const eventDetail = useSelector((state) => state.eventDetail);

    const {
      error: detailEventError,
      loading: detailEventLoading,
      event,
    } = eventDetail;

    useEffect(() => {
      dispatch(eventDetailAction(id, slug));
    }, [dispatch]);

    return (
      <div>
        <div className="productivity_main">
          <div className="productivity_advertise"></div>
          {detailEventLoading ? (
            <Loaders />
          ) : detailEventError ? (
            <ErrorMessage type="error" error={detailEventError} />
          ) : (
            <div className="productivity_cards">
              <ProductivityDetail tech={event} model={"Event"} />
            </div>
          )}
          <div className="productivity_advertise">
            {/* <Advertise tech={techs} /> */}
          </div>
        </div>

        <Footers />
      </div>
    );
  };

export default AdvertiseDetail;
