import {
  TECH_LIST_REQUEST,
  TECH_LIST_SUCCESS,
  TECH_LIST_FAIL,
} from "../constants/tech";

export const techListReducer = (state = { techs: [] }, action) => {
  switch (action.type) {
    case TECH_LIST_REQUEST:
      return { loading: true, techs: [] };
    case TECH_LIST_SUCCESS:
      return {
        loading: false,
        techs: action.payload,
      };

    case TECH_LIST_FAIL:
      return { loading: false, error: action.payload };

    default:
      return state;
  }
};
