import React from "react";
import "../css_styles/Table.css";
import { Link } from "react-router-dom";
import ErrorMessage from "../components/ErrorMessage";
import Loaders from "../components/Loader";


const Table = ({
   createModelError, 
   modelCreateHandler,
   listModelLoading,
   listModelError,
   deleteModelError,
   listModel,
   deleteModelLoading,
   modelDeleteHandler


 }) => {
  // const cats = cat;

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        margin: "50px",
      }}
    >
      <div>
        {createModelError ? (
          <ErrorMessage type="error" error={createModelError} />
        ) : createModelError ? (
          <Loaders />
        ) : (
          <button onClick={modelCreateHandler}>Create Hotels Post</button>
        )}
      </div>

      {listModelLoading ? (
        <Loaders />
      ) : listModelError ? (
        <ErrorMessage type="error" error={listModelError} />
      ) : (
        <div className="post_table">
          {deleteHotelError && (
            <ErrorMessage type="error" error={deleteHotelError} />
          )}
          <table className="post_scroll">
            <thead>
              <tr className="post_header">
                <th>id</th>
                <th>title</th>
                <th>isApproved</th>
                <th>edit</th>
                <th>delete</th>
              </tr>
            </thead>
            <tbody>
              {listHotel.map((hotel) => (
                <tr>
                  <td>{hotel.id}</td>
                  <td>{hotel.title}</td>
                  <td>{hotel.isApproved ? "Yes" : "No"}</td>
                  <Link to={`/hotel-update/${hotel.id}`}>
                    <td>edit</td>
                  </Link>
                  {deleteHotelLoading ? (
                    <Loaders />
                  ) : (
                    <td onClick={() => hotelDeleteHandler(hotel.id)}>
                      Delete
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Table;
