import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Footers from "../components/Footers";
import "../css_styles/DetailProductivity.css";
import ProductivityDetail from "../components/ProductivityDetail";
import { useParams } from "react-router-dom";

import { shopDetailAction } from "../actions/advertiseActions";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

const ShopDetail = ({ match, history, location }) => {
    const params = useParams();
    const id = params.id;

    const dispatch = useDispatch();
    const shopDetail = useSelector((state) => state.shopDetail);

    const {
      error:   detailShopError,
      loading: detailShopLoading,
      shop :   detailShop,
    } = shopDetail;

    useEffect(() => {
      dispatch(shopDetailAction(id));
    }, [dispatch]);

    return (
      <div>
        <div className="productivity_main">
          <div className="productivity_advertise"></div>
          {detailShopLoading ? (
            <Loaders />
          ) : detailShopError ? (
            <ErrorMessage type="error" error={detailShopError} />
          ) : (
            <div className="productivity_cards">
              <ProductivityDetail tech={detailShop} model={"shop"} />
            </div>
          )}
          <div className="productivity_advertise">
            {/* <Advertise tech={techs} /> */}
          </div>
        </div>

        <Footers />
      </div>
    );
  };

export default ShopDetail;
