import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

// CSS IMPORT
import "../css_styles/Celeb.css";
// COMPONENT IMPORT
import CelebCard from "../components/CelebCard";
import Event from "../components/Event";
import Footers from "../components/Footers";
import Banners from "../components/Banners";
import Categories from "../components/Categories";
import CelebCarousel from "../components/CelebCarousel";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";
// ACTION IMPORT
import { celebrityListAction } from "../actions/advertiseActions";

const celeb = {
  title: "title",
  urlToImage:
    "https://staticg.sportskeeda.com/editor/2021/11/caca5-16370851386444-1920.jpg",
  description: "description",
  author: "author",
  content: "content",
};
const Celeb = () => {
  const dispatch = useDispatch();

  // CELEBRITIES PORTIONS
  const celebrityList = useSelector((state) => state.celebrityList);
  const {
    error: listCelebrityError,
    loading: listCelebrityLoading,
    celebrities,
  } = celebrityList;

  // EVENT PORTIONS

  const eventList = useSelector((state) => state.eventList);
  const {
    error: listEventError,
    loading: listEventLoading,
    events,
  } = eventList;


  useEffect(() => {
    dispatch(celebrityListAction());
  },[ dispatch,]);

  return (
    <div>
      <CelebCarousel celeb={celeb} />
      <Banners />
      <Categories />
      <div className="celeb_screen">
        <div className="celeb_event">
          <Event celeb={celeb} />
        </div>

        {listCelebrityLoading ? (
          <Loaders />
        ) : listCelebrityError ? (
          <ErrorMessage type="error" error={listCelebrityError} />
        ) : (
          <div className="celeb_song">

            {celebrities.map((celebrity) => (
              <CelebCard celebrity={celebrity} />
            ))}
          </div>
        )}

        <div className="celeb_event">Hi</div>
      </div>
      <Footers />
    </div>
  );
};

export default Celeb;
