import React, { useEffect } from "react";
import { useNavigate, useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

// COMPONENT IMPORT
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

// CSS IMPORT
import "../css_styles/Advertise.css";

// CONSTANT IMPORT
import {
  JOBS_CREATE_RESET,
  JOBS_DELETE_RESET,
} from "../constants/productivityConstants";

// ACTION IMPORT
import {
  jobListAction,
  jobCreateAction,
  jobDeleteAction,
} from "../actions/advertiseActions2";



const JobCreate = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const params = useParams();
  const { id } = params;


  // JOB CREATE

  const jobCreate = useSelector((state) => state.jobCreate);
  const {
    error:     createJobError,
    loading:   createJobLoading,
    job     :  createJob,
    success:   createJobSuccess,
  } = jobCreate;

  // JOB LIST
  const jobList = useSelector((state) => state.jobList);
  const {
    error:      listJobError,
    loading:    listJobLoading,
    jobs   :    listJob,
  } = jobList;

  // JOB DELETE
  const jobDelete = useSelector((state) => state.jobDelete);
  const {
    error:     deleteJobError,
    loading:   deleteJobLoading,
    job :      deleteJob,
    success:   deleteJobSuccess,
  } = jobDelete;


  // USEEFFECT

  useEffect(() => {
    dispatch(jobListAction());
    dispatch({ type: JOB_CREATE_RESET });
    dispatch({ type: JOB_DELETE_RESET });

    if (createResellerSuccess) {
      navigate(`/reseller-update/${createReseller.id}`);
    }
  }, [dispatch, createResellerSuccess, deleteResellerSuccess]);


  // CREATE ADVERTISE HANDLER
  const createProductHandler = () => {
    dispatch(resellerCreateAction());
  };

  // DELETE ADVERTISE HANDLER
  const eventDeleteHandler = (id) => {
    if (window.confirm("Are You Sure You Want To Delete This Items")) {
      dispatch(resellerDeleteAction(id));
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        margin: "50px",
      }}
    >
      <div>
        {createResellerError ? (
          <ErrorMessage type="error" error={createResellerError} />
        ) : createResellerError ? (
          <Loaders />
        ) : (
          <button onClick={createProductHandler}>Create Jobs Post</button>
        )}
      </div>

      {listResellerLoading ? (
        <Loaders />
      ) : listResellerError ? (
        <ErrorMessage type="error" error={listResellerError} />
      ) : (
        <div className="post_table">
          {deleteResellerError && (
            <ErrorMessage type="error" error={deleteResellerError} />
          )}
          <table className="post_scroll">
            <thead>
              <tr className="post_header">
                <th>id</th>
                <th>title</th>
                <th>isApproved</th>
                <th>edit</th>
                <th>delete</th>
              </tr>
            </thead>
            <tbody>
              {listReseller.map((reseller) => (
                <tr>
                  <td>{reseller.id}</td>
                  <td>{reseller.title}</td>
                  <td>{reseller.isApproved ? "Yes" : "No"}</td>
                  <Link to={`/reseller-update/${reseller.id}`}>
                    <td>edit</td>
                  </Link>
                  {deleteResellerLoading ? (
                    <Loaders />
                  ) : (
                    <td onClick={() => eventDeleteHandler(reseller.id)}>
                      Delete
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default JobCreate;
