// import "../css_styles/Banner.css";
import Pagination from '@mui/material/Pagination';

import { Grid, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";
import {
  FormControl,
  InputLabel,
  Input,
  FormHelperText,
  Button,
} from "@mui/material";

import TextareaAutosize from "@mui/material/TextareaAutosize";

const useStyles = makeStyles({
  root: {
    background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)",
    border: 0,
    borderRadius: 3,
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
    color: "black",
    padding: "0 30px",
    backgrounColor: "red",
    flexWrap: "wrap",
    // height: '300px',
  },
  title1: {
    fontSize: "20px",
    padding: "5px 5px 0px 5px",
    color: "black",
    fontWeight: "bold",
  },
  title2: {
    fontSize: "16px",
    padding: "5px 5px 0px 5px",
    color: "black",
    margin: "20px",
    fontWeight: "bold",
    fontStyle: "italic",
  },
  gridHeader: {
    display:'flex',
    justifyContent: "center",
    textAlign: "center",
    alignItems: "center",
    marginTop: "10px",
    marginBottom: "10px",
    backgrounColor: "white",
    // border: "1px solid black",

    // borderRadius: "5px",
  },

  gridHeader1: {
    display: "flex",
    justifyContent: "center",
    textAlign: "center",
    alignItems: "center",
  },
  search: {
    "&:hover": {
      background: "green",
      color: "white",
      fontWeight: "900",
    },
  },

  button: {
    "&:hover": {
      background: "green",
      color: "white",
      fontWeight: "900",
    },
  },
  paginate:{
    "&:hover": {
      background: "green",
      color: "white",
      fontWeight: "900",
    },

  }
});

const Paginate = ({pages, page, keyword=''}) => {
  const navigate = useNavigate()
  const location = useLocation()
  const pathname = location.pathname
  if (keyword) {
    keyword = keyword.split('?keyword=')[1].split('&')[0]
  }

  const handleChange = () => {
    navigate(`${pathname}?keyword=${keyword}&page=${page}`)
  }
  console.log(pages, "pages")
  console.log(page, "page")
  // console.log(pages, "pages")


  const classes = useStyles();
  const list = [...Array(pages).keys()];
  console.log("list", list)


  return (pages > 1 && (
  //   <Pagination
  //   count={list}
  //   size="large"
  //   page={page}
  //   variant="outlined"
  //   shape="rounded"
  //   onChange={handleChange}
  // />

      <Grid className={classes.gridHeader1}>
        <Grid className={classes.gridHeader} xs={12} sm={12} lg={6} md={6} item>
          {[...Array(pages).keys()].map((x) => (
            <Link
            style={{textDecoration:'none'}}
            key = {x +1}
            to={`/?keyword=${keyword}&page=${x + 1}`}>
              <div className={classes.paginate} >

              <p className={classes.paginate} style={{color:"black", padding: '8px 16px', textDecoration:'none'}}>{x + 1}</p>
              </div>
              {/* <Pagination  page={x+1} variant="outlined" shape="rounded"/> */}
            </Link>
          ))}
        </Grid>
      </Grid>

  ));
};

export default Paginate;
