import {
  TECH_LIST_REQUEST,
  TECH_LIST_SUCCESS,
  TECH_LIST_FAIL,
} from "../constants/tech";

import axios from 'axios'

export const listTechs =() => async (dispatch) =>  {
    try {
        dispatchEvent({ type: TECH_LIST_REQUEST})
        const {data } = await axios.get('http://127.0.0.1:8000/api/technology/list/')

        dispatch({
            type: TECH_LIST_SUCCESS,
            payload:data
        })

    } catch (error) {
        dispatch({
            type: TECH_LIST_FAIL,
            payload: error.response && error.response.data.detail
                ? error.response.data.detail
                :error.message,
        })
    }
}
