  ADVERTISE_CREATE_RESET,
  ADVERTISE_DELETE_RESET,

  CELEBRITY_CREATE_RESET,
  CELEBRITY_DELETE_RESET,

  EVENT_CREATE_RESET,
  EVENT_DELETE_RESET,
} from "../constants/productivityConstants";

// ACTION IMPORT

