import React, { useEffect } from "react";
import "../css_styles/Tourisms.css";
import ShopCard from "../components/ShopCard";
import Footers from "../components/Footers";
import Banners from "../components/Banners";
import Categories from "../components/Categories";
import CelebCarousel from "../components/CelebCarousel";
import { useSelector, useDispatch } from "react-redux";
import { shopListAction } from "../actions/advertiseActions";

const celeb = {
  title: "title",
  urlToImage:
    "https://staticg.sportskeeda.com/editor/2021/11/caca5-16370851386444-1920.jpg",
  description: "description",
  author: "author",
  content: "content",
};
const Shops = () => {
  const dispatch = useDispatch();
  const { shopList } = useSelector((state) => state.shopList);

  const {
    error: listShopError,
    loading: listShopLoading,
    shops: listShop,
  } = shopList;

  useEffect(() => {
    dispatch(shopListAction());
  }, [dispatch]);

  return (
    <div>
      <CelebCarousel celeb={celeb} />
      <Banners />
      <Categories />
      <div className="tourist_screen">
        <div className="tourist_event">Hi</div>
        <div className="tourist_main">
          {shopList.map((shop) => (
            <ShopCard key={shop.id} shop={shop} />
          ))}
        </div>
        <div className="tourist_event">Hi</div>
      </div>
      <Footers />
    </div>
  );
};

export default Shops;
