    fontSize: "16px",
    padding: "5px 5px 0px 5px",
    color: "black",
    margin: "20px",
    fontWeight: "bold",
    fontStyle: "italic",
  },
  gridHeader: {
    display:'flex',
    justifyContent: "center",
    textAlign: "center",
    alignItems: "center",
    marginTop: "10px",
    marginBottom: "10px",
    backgrounColor: "white",
    // border: "1px solid black",

    // borderRadius: "5px",
  },

  gridHeader1: {
    display: "flex",
    justifyContent: "center",
    textAlign: "center",
    alignItems: "center",
  },
  search: {
    "&:hover": {
      background: "green",
      color: "white",
      fontWeight: "900",
    },
  },

  button: {
    "&:hover": {
      background: "green",
      color: "white",
      fontWeight: "900",
    },
  }
});

const Paginate = ({pages, page, keyword=''}) => {
  const navigate = useNavigate()

  const handleChange = () => {
    navigate
  }


  const classes = useStyles();


  return (pages > 1 && (
    <Pagination
