import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate, Link } from "react-router-dom";

// COMPONENT IMPORT
import AdminSidebar from "../components/AdminSidebar";
import PerformanceChart from "../components/PerformanceChart";
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";
import Table from "../components/Table";

// CSS IMPORT
import "../css_styles/AdminDashboard.css";
import "../css_styles/UserTable.css";
import "../css_styles/Advertise.css";

import { userListActions } from "../actions/userActions";

// CONSTANT IMPORT
import {
  ADVERTISE_CREATE_RESET,
  ADVERTISE_DELETE_RESET,
  CELEBRITY_CREATE_RESET,
  CELEBRITY_DELETE_RESET,
  EVENT_CREATE_RESET,
  EVENT_DELETE_RESET,
  HOTEL_CREATE_RESET,
  HOTEL_DELETE_RESET,
} from "../constants/productivityConstants";

// ACTION IMPORT

import {
  advertiseListAction,
  advertiseCreateAction,
  advertiseDeleteAction,
  celebrityListAction,
  celebrityCreateAction,
  celebrityDeleteAction,
  eventListAction,
  eventCreateAction,
  eventDeleteAction,
} from "../actions/advertiseActions";

// ACTION IMPORT
import {
  hotelListAction,
  hotelCreateAction,
  hotelDeleteAction,
} from "../actions/advertiseActions2";

const AdminDashboard = ({ tech }) => {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const navigate = useNavigate();
  const dispatch = useDispatch();

  const userList = useSelector((state) => state.userList);
  const { error: listUserError, loading: listUserLoading, users } = userList;

  // ADVERTISE CREATE

  const advertiseCreate = useSelector((state) => state.advertiseCreate);
  const {
    error: advertiseCreateError,
    loading: advertiseCreateLoading,
    advertise: advertise,
    success: advertiseCreateSuccess,
  } = advertiseCreate;

  // ADVERTISE LIST
  const advertiseList = useSelector((state) => state.advertiseList);
  const {
    error: listAdvertiseError,
    loading: listAdvertiseLoading,
    advertises: listAdvertise,
  } = advertiseList;

  // ADVERTISE DELETE
  const advertiseDelete = useSelector((state) => state.advertiseDelete);
  const {
    error: deleteAdvertiseError,
    loading: deleteAdvertiseLoading,
    advertise: deleteAdvertise,
    success: deleteAdvertiseSuccess,
  } = advertiseDelete;

  // CELEBRITIES CREATE

  const celebrityCreate = useSelector((state) => state.celebrityCreate);
  const {
    error: createCelebrityError,
    loading: createCelebrityLoading,
    celebrity: createCelebrity,
    success: createCelebritySuccess,
  } = celebrityCreate;

  // CELEBRITIES LIST
  const celebrityList = useSelector((state) => state.celebrityList);
  const {
    error: listCelebrityError,
    loading: listCelebrityLoading,
    celebrities: listCelebrities,
  } = celebrityList;

  // CELEBRITIES DELETE
  const celebrityDelete = useSelector((state) => state.celebrityDelete);
  const {
    error: deleteCelebrityError,
    loading: deleteCelebrityLoading,
    celebrity: deleteCelebrity,
    success: deleteCelebritySuccess,
  } = celebrityDelete;

  // EVENT CREATE

  const eventCreate = useSelector((state) => state.eventCreate);
  const {
    error: createEventError,
    loading: createEventLoading,
    event: createEvent,
    success: createEventSuccess,
  } = eventCreate;

  // EVENT LIST
  const eventList = useSelector((state) => state.eventList);
  const {
    error: listEventError,
    loading: listEventLoading,
    events: listEvent,
  } = eventList;

  // EVENT DELETE
  const eventDelete = useSelector((state) => state.eventDelete);
  const {
    error: deleteEventError,
    loading: deleteEventLoading,
    event: deleteEvent,
    success: deleteEventSuccess,
  } = eventDelete;

  // HOTEL CREATE

  const hotelCreate = useSelector((state) => state.hotelCreate);
  const {
    error: createHotelError,
    loading: createHotelLoading,
    hotel: createHotel,
    success: createHotelSuccess,
  } = hotelCreate;

  // HOTEL LIST
  const hotelList = useSelector((state) => state.hotelList);
  const {
    error: listHotelError,
    loading: listHotelLoading,
    hotels: listHotel,
  } = hotelList;

  // HOTEL DELETE
  const hotelDelete = useSelector((state) => state.hotelDelete);
  const {
    error: deleteHotelError,
    loading: deleteHotelLoading,
    hotel: deleteHotel,
    success: deleteHotelSuccess,
  } = hotelDelete;

  useEffect(() => {
    if (!userInfo) {
      navigate("/");
    }
    dispatch(userListActions());
    // ADVERTISE PORTION
    dispatch(advertiseListAction());
    dispatch({ type: ADVERTISE_CREATE_RESET });
    dispatch({ type: ADVERTISE_DELETE_RESET });

    if (advertiseCreateSuccess) {
      navigate(`/user-post-create/${advertise.id}`);
    }
    // CELEBRITY PORTION
    dispatch(celebrityListAction());
    dispatch({ type: CELEBRITY_CREATE_RESET });
    dispatch({ type: CELEBRITY_DELETE_RESET });

    if (createCelebritySuccess) {
      navigate(`/celebrity-update/${createCelebrity.id}`);
    }

    // EVENT PORTIONS
    dispatch(eventListAction());
    dispatch({ type: EVENT_CREATE_RESET });
    dispatch({ type: EVENT_DELETE_RESET });

    if (createEventSuccess) {
      navigate(`/event-update/${createEvent.id}`);
    }

    // Hotel PORTION

    dispatch(hotelListAction());
    dispatch({ type: HOTEL_CREATE_RESET });
    dispatch({ type: HOTEL_DELETE_RESET });

    if (createHotelSuccess) {
      navigate(`/hotel-update/${createHotel.id}`);
    }
  }, [
    dispatch,
    userInfo,
    advertiseCreateSuccess,
    deleteAdvertiseSuccess,
    createCelebritySuccess,
    deleteCelebritySuccess,
    createEventSuccess,
    deleteEventSuccess,
    createHotelSuccess,
    deleteHotelSuccess,
  ]);

  // CREATE ADVERTISE HANDLER
  const createProductHandler = () => {
    dispatch(advertiseCreateAction());
  };

  // DELETE ADVERTISE HANDLER
  const advertiseDeleteHandler = (id) => {
    if (window.confirm("Are You Sure You Want To Delete This Items")) {
      dispatch(advertiseDeleteAction(id));
    }
  };

  // CREATE CELEBRITY HANDLER
  const celebrityCreateHandler = () => {
    dispatch(celebrityCreateAction());
  };

  // DELETE CELEBRITY HANDLER
  const celebrityDeleteHandler = (id) => {
    if (window.confirm("Are You Sure You Want To Delete This Items")) {
      dispatch(celebrityDeleteAction(id));
    }
  };

  // CREATE EVENT HANDLER
  const eventCreateHandler = () => {
    dispatch(eventCreateAction());
  };

  // DELETE EVENT HANDLER
  const eventDeleteHandler = (id) => {
    if (window.confirm("Are You Sure You Want To Delete This Items")) {
      dispatch(eventDeleteAction(id));
    }
  };

  // CREATE HOTEL HANDLER
  const hotelCreateHandler = () => {
    dispatch(hotelCreateAction());
  };

  // DELETE HOTEL HANDLER
  const hotelDeleteHandler = (id) => {
    if (window.confirm("Are You Sure You Want To Delete This Items")) {
      dispatch(hotelDeleteAction(id));
    }
  };

  return (
    <div className="admin">
      <div className="admin_top">Admin Headers</div>

      <div className="admin_main">
        <div className="admin_sidebar">
          <AdminSidebar />
        </div>
        <div className="admin_body">
          <PerformanceChart />
          <div className="admin_status">
            <div className="admin_status1">
              <div className="admin_status1_items">Icon</div>
              <div className="admin_status1_items">
                Visitors
                <br />
                1235
              </div>
            </div>
            <div className="admin_status1">
              <div className="admin_status1_items">Icon</div>
              <div className="admin_status1_items">
                Page Views
                <br />
                1235
              </div>
            </div>
          </div>
          <div className="users_post">
            {/* ************************BIG CONTENT FOR ADMIN USER ************************** */}
            {/* ADVERTISEMENT */}
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: "column",
                margin: "50px",
              }}
            >
              <div>
                {advertiseCreateError ? (
                  <ErrorMessage type="error" error={advertiseCreateError} />
                ) : advertiseCreateLoading ? (
                  <Loaders />
                ) : (
                  <button onClick={createProductHandler}>
                    Create Advertise
                  </button>
                )}
              </div>

              {listAdvertiseLoading ? (
                <Loaders />
              ) : listAdvertiseError ? (
                <ErrorMessage type="error" error={listAdvertiseError} />
              ) : (
                <div className="post_table">
                  {deleteAdvertiseError && (
                    <ErrorMessage type="error" error={deleteAdvertiseError} />
                  )}
                  <table className="post_scroll">
                    <thead>
                      <tr className="post_header">
                        <th>id</th>
                        <th>title</th>
                        <th>isApproved</th>
                        <th>edit</th>
                        <th>delete</th>
                      </tr>
                    </thead>
                    <tbody>
                      {listAdvertise.map((advertise) => (
                        <tr>
                          <td>{advertise.id}</td>
                          <td>{advertise.title}</td>
                          <td>{advertise.isApproved ? "Yes" : "No"}</td>
                          <Link to={`/user-post-create/${advertise.id}`}>
                            <td>edit</td>
                          </Link>
                          {deleteAdvertiseLoading ? (
                            <Loaders />
                          ) : (
                            <td
                              onClick={() =>
                                advertiseDeleteHandler(advertise.id)
                              }
                            >
                              Delete
                            </td>
                          )}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
            {/* CELEBRITY */}
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: "column",
                margin: "50px",
              }}
            >
              <div>
                {createCelebrityError ? (
                  <ErrorMessage type="error" error={createCelebrityError} />
                ) : createCelebrityError ? (
                  <Loaders />
                ) : (
                  <button onClick={celebrityCreateHandler}>
                    Create Celebrity Post
                  </button>
                )}
              </div>

              {listCelebrityLoading ? (
                <Loaders />
              ) : listCelebrityError ? (
                <ErrorMessage type="error" error={listCelebrityError} />
              ) : (
                <div className="post_table">
                  {deleteCelebrityError && (
                    <ErrorMessage type="error" error={deleteCelebrityError} />
                  )}
                  <table className="post_scroll">
                    <thead>
                      <tr className="post_header">
                        <th>id</th>
                        <th>title</th>
                        <th>isApproved</th>
                        <th>edit</th>
                        <th>delete</th>
                      </tr>
                    </thead>
                    <tbody>
                      {listCelebrities.map((celebrity) => (
                        <tr>
                          <td>{celebrity.id}</td>
                          <td>{celebrity.title}</td>
                          <td>{celebrity.isApproved ? "Yes" : "No"}</td>
                          <Link to={`/celebrity-update/${celebrity.id}`}>
                            <td>edit</td>
                          </Link>
                          {deleteCelebrityLoading ? (
                            <Loaders />
                          ) : (
                            <td
                              onClick={() =>
                                celebrityDeleteHandler(celebrity.id)
                              }
                            >
                              Delete
                            </td>
                          )}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
            {/* EVENT PORTION */}
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: "column",
                margin: "50px",
              }}
            >
              <div>
                {createEventError ? (
                  <ErrorMessage type="error" error={createEventError} />
                ) : createEventError ? (
                  <Loaders />
                ) : (
                  <button onClick={eventCreateHandler}>
                    Create Event Post
                  </button>
                )}
              </div>

              {listEventLoading ? (
                <Loaders />
              ) : listEventError ? (
                <ErrorMessage type="error" error={listEventError} />
              ) : (
                <div className="post_table">
                  {deleteEventError && (
                    <ErrorMessage type="error" error={deleteEventError} />
                  )}
                  <table className="post_scroll">
                    <thead>
                      <tr className="post_header">
                        <th>id</th>
                        <th>title</th>
                        <th>isApproved</th>
                        <th>edit</th>
                        <th>delete</th>
                      </tr>
                    </thead>
                    <tbody>
                      {listEvent.map((event) => (
                        <tr>
                          <td>{event.id}</td>
                          <td>{event.title}</td>
                          <td>{event.isApproved ? "Yes" : "No"}</td>
                          <Link to={`/event-update/${event.id}`}>
                            <td>edit</td>
                          </Link>
                          {deleteEventLoading ? (
                            <Loaders />
                          ) : (
                            <td onClick={() => eventDeleteHandler(event.id)}>
                              Delete
                            </td>
                          )}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
            {/* HOTEL PORTION */}
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: "column",
                margin: "50px",
              }}
            >
              <div>
                {createHotelError ? (
                  <ErrorMessage type="error" error={createHotelError} />
                ) : createHotelError ? (
                  <Loaders />
                ) : (
                  <button onClick={hotelCreateHandler}>
                    Create Hotels Post
                  </button>
                )}
              </div>

              {listHotelLoading ? (
                <Loaders />
              ) : listHotelError ? (
                <ErrorMessage type="error" error={listHotelError} />
              ) : (
                <div className="post_table">
                  {deleteHotelError && (
                    <ErrorMessage type="error" error={deleteHotelError} />
                  )}
                  <table className="post_scroll">
                    <thead>
                      <tr className="post_header">
                        <th>id</th>
                        <th>title</th>
                        <th>isApproved</th>
                        <th>edit</th>
                        <th>delete</th>
                      </tr>
                    </thead>
                    <tbody>
                      {listHotel.map((hotel) => (
                        <tr>
                          <td>{hotel.id}</td>
                          <td>{hotel.title}</td>
                          <td>{hotel.isApproved ? "Yes" : "No"}</td>
                          <Link to={`/hotel-update/${hotel.id}`}>
                            <td>edit</td>
                          </Link>
                          {deleteHotelLoading ? (
                            <Loaders />
                          ) : (
                            <td onClick={() => hotelDeleteHandler(hotel.id)}>
                              Delete
                            </td>
                          )}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>


            <Table createHotelError={createHotelError}
                  hotelCreateHandler={hotelCreateHandler}
                  listHotelLoadin={listHotelLoading}
                  listHotelError={listHotelError}
                  deleteHotelError={deleteHotelError}
                  listHotel={listHotel}
                  deleteHotelLoading={deleteHotelLoading}
   hotelDeleteHandler
    />
            {/* ************************BIG CONTENT FOR ADMIN USER ************************** */}
          </div>
        </div>

        {listUserError && <ErrorMessage type="error" error={listUserError} />}
        {listUserLoading && <Loaders />}
        <div className="users">
          <div className="user_table">
            <table className="user_scroll">
              <thead>
                <tr className="user_header">
                  <th>user</th>
                  <th>username</th>
                  <th>password</th>
                </tr>
              </thead>
              <tbody>
                {users &&
                  users.map((user) => (
                    <tr>
                      <td>{user.username}</td>
                      <td>{user.email}</td>
                      <td>{user.password}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
