import React, { useState, useEffect } from "react";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { useLocation, useParams } from "react-router-dom";
import { createTechs } from "../actions/techActions";

// import TechCreateNewsApi from "../admin-screen/TechCreateNewsApi";
import { advertiseDetailAction } from "../actions/advertiseActions";

import "../css_styles/TechCreate.css";

const UserPostUpdate = () => {
  const params = useParams();
  const advertise_id = params.id;

  // const [techs, setTechs] = useState([]);
  // const newsApiKey = "d049a308e4634c8b8a28ce3b4b3059be";
  // const techNewsApi = () => async () => {
  //   console.log("Fetching api");
  //   const { data } = await axios.get(
  //     `https://newsapi.org/v2/top-headlines?country=in&category=technology&apiKey=${newsApiKey}`
  //   );
  //   console.log("data", data);
  //   console.log("techs1", techs);
  //   setTechs(data.articles);
  //   console.log("techs2", techs);
  // };

  // techs.map((tech) => console.log("techs.author", tech.author));

  // const userLogin = useSelector(state => state.userLogin)
  // const {userInfo} = userLogin;
  const [category, setCategory] = useState("");
  const [country, setCountry] = useState("");
  const [state, setState] = useState("");
  const [address, setAddress] = useState("");
  const [contact, setContact] = useState("");
  const [image, setImage] = useState("");
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const dispatch = useDispatch();

  const advertiseDetail = useSelector((state) => state.advertiseDetail);
  const {
    error: advertiseDetailError,
    loading: advertiseDetailLoading,
    advertise: detailAdvertise,
  } = advertiseDetail;

  useEffect(() => {
    if (!detailAdvertise.id !== Number(advertise_id )) {
      dispatch(advertiseDetailAction(advertise_id ));
      console.log("run dispatch")
    } else {
      console.log("runSet")
      setCategory(detailAdvertise.category);
      setCountry(detailAdvertise.country);
      setState(detailAdvertise.state);
      setAddress(detailAdvertise.address);
      setContact(detailAdvertise.contact);
      setImage(detailAdvertise.image);
      setTitle(detailAdvertise.title);
      setContent(detailAdvertise.content);
    }
  }, [dispatch, advertise_id ]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(
      createTechs(
        category,
        country,
        state,
        address,
        contact,
        image,
        title,
        content
      )
    );
  };

  return (
    <>
      <div className="techcreate">
        <div className="form">
          <form onSubmit={submitHandler}>
            <div className="text">
              <div className="subtitle">Let's Update Models</div>
            </div>
            <div className="input-container">
              <label>Category</label>
              <input
                id="category"
                className="input"
                type="text"
                placeholder="categorygfhfg"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              />
            </div>
            <div className="input-container">
              <label>Country</label>
              <input
                id="country"
                className="input"
                type="text"
                placeholder="country"
                value={country}
                onChange={(e) => setCountry(e.target.value)}
              />
            </div>
            <div className="input-container">
              <label>State</label>
              <input
                id="state"
                className="input"
                type="state"
                placeholder="state"
                value={state}
                onChange={(e) => setState(e.target.value)}
              />
            </div>
            <div className="input-container ic2">
              <label>Address</label>
              <input
                id="address"
                className="input"
                type="address"
                placeholder="Url"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
              />
            </div>
            <div className="input-container ic2">
              <label>Contact</label>
              <input
                id="contact"
                className="input"
                type="text"
                placeholder="contact"
                value={contact}
                onChange={(e) => setContact(e.target.value)}
              />
            </div>
            <div className="input-container ic2">
              <label>Title</label>
              <input
                id="title"
                className="input"
                type="text"
                placeholder="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>

            <div className="input-container ic2">
              <label>Content</label>
              <textarea
                id="content"
                className="input"
                type="textfield"
                placeholder="Content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
            </div>

            <div className="input-container ic2">
              <label>Images</label>
              <input
                id="image"
                className="input"
                type="text"
                placeholder="image"
                value={image}
                onChange={(e) => setImage(e.target.value)}
              />
            </div>
            <div className="input-container ic2">
              <button className="button_input" type="submit">
                Submit
              </button>
            </div>
          </form>
          {/* <div className="techcreate">
            {<TechCreateNewsApi/>}

          </div> */}
        </div>
      </div>
    </>
  );
};

export default UserPostUpdate;
