import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { userLoginActions } from "../actions/userActions";
import {Link} from "react-router-dom"
import {useNavigate} from "react-router-dom"
import { Grid,Paper,  Typography, Avatar, TextField,Button } from "@mui/material";
import FromControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import PersonOutlineIcon from '@mui/icons-material/PersonOutline';
import Loaders from "../components/Loader";
import ErrorMessage from "../components/ErrorMessage";

import "../css_styles/LoginScreen.css";

const LoginScreens = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const userLogin = useSelector((state) => state.userLogin)

  const {
    error:loginUserError,
    loading:loginUserLoading,
    userInfo
  } = userLogin


  useEffect(() => {
    if(userInfo){
      navigate(-1)
    }
  })

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(userLoginActions(username, password));
  };

  return (
    <>
    <Grid>
      <Paper elevation={10} >
    <Grid className="gridHeader" >
        <Grid align='center' >
          {loginUserLoading && <Loaders/>}
          {loginUserError && <ErrorMessage type="error" error={loginUserError} />}
          <Avatar><PersonOutlineIcon/></Avatar>
          <h2>sign In</h2>
        </Grid>
        <TextField
          id="username"
          className="input"
          type="text"
          placeholder="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          fullWidth
        />
        <TextField
          id="password"
          className="input"
          type="password"
          placeholder="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          fullWidth 
          required
        />
        <FromControlLabel
          control={
            <Checkbox
              name="checkedB"
              color="primary"
              />
            }
          label="Remember me"
          />
        <Button onClick={submitHandler} type='submit' color='success' variant='contained' fullWidth>
          Sign IN
        </Button>

          <Link to="/forgotPassword">
            <Typography>
              Forgot password ?
            </Typography>
            </Link>
          <Link to="/register">       
            <Typography>
              sign up
            </Typography>
          </Link>
        </Grid>
      </Paper>
    </Grid>



    </>
  );
};

export default LoginScreens;
